sp_widget.client_script:
api.controller=function(spModal) {
  /* widget controller */
  var c = this;
  c.data.allow_modal = true;

  c.openModal = function(_options){
	  _options.modal = true;  
	  if(c.data.allow_modal){
		//options.display = 'options-display';
		//options.type = 'text-only';
		spModal.open({
			title: c.data.modal_title || 'Leave Feedback',
			widget: 'feedback-body',
			widgetInput: _options,
			buttons: _options.buttons,
			size: _options.size
		}).then(function(response){
			console.log(response);
		})
	  }
  };
};

