sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
  data.options = options;
	data.options.buttons = [({
					label: 'Send feedback',
					primary: true,
					url: '',
					custom_parameter: 'Hello world'
		})];
  data.display_location = options.display_location.replace('_',' ');
  data.display = options.display;
})();
